  changePhoto() {
    this.setState({ imgSrc: "./kitty.jpeg" });
  }

  render() {
    return (
      <header className="pageLabel">
        Media Acessibility
        <div>
          <DisplayAnImage
            alignSelf="center"
            src={this.state.imgSrc}
            caption={this.state.caption}
          ></DisplayAnImage>
          <TextInput
            placeholder={this.state.caption}
            multiline
            numberOfLines={4}
          />
          <Button
            className="Cancel"
            title="Cancel"
            titleStyle={{
              color: "red",
              fontsize: "16",
            }}
          ></Button>
          <Button className="Skip" title="Next Photo"></Button>

          <Button
            className="Submit"
            /*onClick={submitCaption}></Button>*/
            title="Submit"
            disabled="true"
            onPress={this.changePhoto}
          ></Button>
        </div>
      </header>
    );
  }
}
export default AddCaption;
