import React from 'react';
import { View, StyleSheet, Button, TextInput } from 'react-native';

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "space-around",
        alignItems: "center",
    },
});

const laterPopUp = () => {
    const popUp = () =>
    Alert.alert(
      "Media Accessibility",
      [
        { 
          text: "Enter Alternative Text",
          onPress: text => console.log(text)
        },
      ],
      "text entered"
    );

    return (
      <View style={styles.container}>
        <popUp/>
      </View>
    );
  }

export default ImageTextInputMultiline;

// below text is https://reactnative.dev/docs/textinput
const ImageTextInput = (props) => {
    return (
      <TextInput
        {...props} // Inherit any props passed to it; e.g., multiline, numberOfLines below
        editable
        maxLength={40}
      />
    );
  }

  const ImageTextInputMultiline = () => {
    const [value, onChangeText] = React.useState('Image Description: Tell us about the image...');

    // If you type something in the text box that is a color, the background will change to that
    // color.
    return (
      <View
        style={{
          backgroundColor: value,
          borderBottomColor: '#000000',
          borderBottomWidth: 1,
        }}>
        <ImageTextInput
          multiline
          numberOfLines={4}
          onChangeText={text => onChangeText(text)}
          value={value}
          style={{padding: 10}}
        />
      </View>
    );
  }
